  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const getLocalResponse = (message) => {
    const lowerMessage = message.toLowerCase();

    if (lowerMessage.match(/^(hi|hello|hey)/)) {
      return "Hello! ðŸ‘‹ I'm your AI Student Result Assistant. I can help you with:\n\nâ€¢ Results and grades\nâ€¢ CGPA calculation\nâ€¢ PDF downloads\nâ€¢ Email notifications\nâ€¢ Excel exports\nâ€¢ System navigation\n\nWhat would you like to know?";
    }

    if (lowerMessage.includes('help')) {
      return "I can help with:\nðŸ“Š Checking results\nðŸ“„ PDF reports\nðŸ“§ Email notifications\nðŸ“ˆ Excel exports\nðŸŽ¯ CGPA calculation\nðŸ” Login issues\n\nJust ask!";
    }

    if (lowerMessage.includes('grade') || lowerMessage.includes('cgpa')) {
      return "**Grading System:**\nðŸŒŸ A+ : 90-100% (CGPA 10.0)\nâ­ A : 80-89% (CGPA 9.0-9.5)\nâœ¨ B+ : 70-79% (CGPA 8.0-8.5)\nðŸ’« B : 60-69% (CGPA 7.0-7.5)\nâš¡ C : 50-59% (CGPA 6.0-6.5)\nðŸ“Œ D : 40-49% (CGPA 5.0-5.5)\nâŒ F : Below 40% (CGPA 0.0)";
    }

    if (lowerMessage.includes('result')) {
      return "**Check Results:**\n1. Login with roll number\n2. Go to Results tab\n3. View grades, marks & CGPA\n4. Download PDF report";
    }

    if (lowerMessage.includes('pdf')) {
      return "**Download PDF:**\n1. Go to dashboard\n2. Click 'Results' tab\n3. Click 'Download PDF'\n4. PDF downloads automatically!";
    }

    if (lowerMessage.includes('email')) {
      return "**Email Notifications:**\nâœ… Student creation â†’ Credentials sent\nâœ… Teacher creation â†’ Credentials sent\nâœ… Result published â†’ Notification sent";
    }

    if (lowerMessage.includes('excel')) {
      return "**Excel Export:**\n1. Go to Students/Teachers/Results\n2. Click 'Export to Excel'\n3. File downloads instantly!";
    }

    if (lowerMessage.includes('login')) {
      return "**Login Info:**\nâ€¢ Admin: admin / admin123\nâ€¢ Student: Roll Number / Password\nâ€¢ Teacher: Teacher ID / Password";
    }

    if (lowerMessage.match(/(thank|thanks)/)) {
      return "You're welcome! ðŸ˜Š Feel free to ask anything else!";
    }

    if (lowerMessage.match(/(bye|goodbye)/)) {
      return "Goodbye! ðŸ‘‹ Have a great day!";
    }

    return null;
  };

  const getGeminiResponse = async (message) => {
    if (GEMINI_API_KEY === 'YOUR_API_KEY_HERE') {
      return "âš ï¸ AI not configured yet!\n\nGet a free API key: https://makersuite.google.com/app/apikey\n\nBut I can still answer questions about results, grades, PDFs, and more!";
    }

    try {
      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GEMINI_API_KEY}`,
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            contents: [{
              parts: [{
                text: `You are a helpful AI assistant for a Student Result Management System. Answer this question concisely: ${message}`
              }]
            }],
            generationConfig: {
              temperature: 0.7,
              maxOutputTokens: 300,
            }
          })
        }
      );

      const data = await response.json();
      const aiText = data.candidates?.[0]?.content?.parts?.[0]?.text;
      return aiText || "I'm not sure. Try asking about results, grades, or system features!";
    } catch (error) {
      return "Connection error! But I can still help with common questions!";
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsTyping(true);

    const localResponse = getLocalResponse(input);

    if (localResponse) {
      setTimeout(() => {
        setMessages(prev => [...prev, { role: 'bot', content: localResponse }]);
        setIsTyping(false);
      }, 600);
    } else {
      const aiResponse = await getGeminiResponse(input);
      setMessages(prev => [...prev, { role: 'bot', content: aiResponse }]);
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  if (!isOpen) {
    return (
      <button className="chatbot-trigger" onClick={() => setIsOpen(true)}>
        <MessageCircle size={24} />
        <span className="chatbot-badge">AI</span>
      </button>
    );
  }

  return (
    <div className={`chatbot-container ${isMinimized ? 'minimized' : ''}`}>
