              <div className="overflow-x-auto rounded-lg border border-gray-200">
                <table className="w-full">
                  <thead className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white">
                    <tr>
                      <th className="px-6 py-4 text-left font-semibold">Teacher ID</th>
                      <th className="px-6 py-4 text-left font-semibold">Name</th>
                      <th className="px-6 py-4 text-left font-semibold">Department</th>
                      <th className="px-6 py-4 text-left font-semibold">Email</th>
                      <th className="px-6 py-4 text-left font-semibold">Phone</th>
                      <th className="px-6 py-4 text-center font-semibold">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-200">
                    {filteredTeachers.map((teacher, index) => (
                      <tr key={teacher.id} className={`${index % 2 === 0 ? 'bg-gray-50' : 'bg-white'} hover:bg-purple-50 transition`}>
                        <td className="px-6 py-4 font-semibold text-purple-600">{teacher.teacherId}</td>
                        <td className="px-6 py-4 font-medium text-gray-900">{teacher.name}</td>
                        <td className="px-6 py-4">
                          <span className="px-3 py-1.5 bg-indigo-100 text-indigo-700 rounded-full text-sm font-semibold">
                            {teacher.department}
                          </span>
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-600">{teacher.email}</td>
                        <td className="px-6 py-4 text-gray-700">{teacher.phone}</td>
                        <td className="px-6 py-4">
                          <div className="flex justify-center gap-3">
                            <button className="text-blue-600 hover:text-blue-800 transition">
                              <Edit className="w-5 h-5" />
                            </button>
                            <button className="text-red-600 hover:text-red-800 transition">
                              <Trash2 className="w-5 h-5" />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Results Tab with CGPA */}
          {activeTab === 'results' && (
            <div className="bg-white rounded-xl shadow-lg p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">Results Management</h2>
                <button className="bg-gradient-to-r from-green-600 to-teal-600 text-white px-6 py-2.5 rounded-lg flex items-center gap-2 hover:shadow-lg transform hover:scale-105 transition">
                  <Download className="w-5 h-5" />
                  Export to Excel
                </button>
              </div>

              <div className="mb-6">
                <div className="relative">
                  <Search className="absolute left-4 top-3.5 w-5 h-5 text-gray-400" />
                  <input
                    type="text"
                    placeholder="Search by student name, roll number, or subject..."
                    value={resultSearch}
                    onChange={(e) => setResultSearch(e.target.value)}
                    className="w-full pl-12 pr-12 py-3.5 border-2 border-gray-200 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition"
                  />
                  {resultSearch && (
                    <button onClick={() => setResultSearch('')} className="absolute right-4 top-3.5 text-gray-400 hover:text-gray-600">
                      <X className="w-5 h-5" />
                    </button>
                  )}
                </div>
              </div>

              <div className="mb-4 text-sm text-gray-600 font-medium">
                Showing {filteredResults.length} of {results.length} results
              </div>

              <div className="overflow-x-auto rounded-lg border border-gray-200">
                <table className="w-full">
                  <thead className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white">
                    <tr>
                      <th className="px-4 py-4 text-left font-semibold">Roll No</th>
                      <th className="px-4 py-4 text-left font-semibold">Student</th>
                      <th className="px-4 py-4 text-left font-semibold">Subject</th>
                      <th className="px-4 py-4 text-center font-semibold">Marks</th>
                      <th className="px-4 py-4 text-center font-semibold">Percentage</th>
                      <th className="px-4 py-4 text-center font-semibold">CGPA</th>
                      <th className="px-4 py-4 text-center font-semibold">Grade</th>
                      <th className="px-4 py-4 text-left font-semibold">Teacher</th>
                      <th className="px-4 py-4 text-center font-semibold">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-200">
                    {filteredResults.map((result, index) => (
                      <tr key={result.id} className={`${index % 2 === 0 ? 'bg-gray-50' : 'bg-white'} hover:bg-purple-50 transition`}>
                        <td className="px-4 py-4 font-semibold text-purple-600">{result.rollNo}</td>
                        <td className="px-4 py-4 font-medium text-gray-900">{result.studentName}</td>
                        <td className="px-4 py-4 text-gray-700">{result.subjectName}</td>
                        <td className="px-4 py-4 text-center">
                          <span className="font-bold text-gray-900">{result.marksObtained}</span>
                          <span className="text-gray-500">/{result.totalMarks}</span>
                        </td>
                        <td className="px-4 py-4 text-center">
                          <span className={`font-bold ${
                            result.percentage >= 90 ? 'text-green-600' :
                            result.percentage >= 70 ? 'text-blue-600' :
                            result.percentage >= 50 ? 'text-orange-600' :
                            'text-red-600'
                          }`}>
                            {result.percentage?.toFixed(2)}%
                          </span>
                        </td>
                        <td className="px-4 py-4 text-center">
                          <span className={`px-4 py-2 rounded-full text-sm font-bold shadow-sm ${
                            result.cgpa >= 9 ? 'bg-green-100 text-green-700 ring-2 ring-green-200' :
                            result.cgpa >= 8 ? 'bg-blue-100 text-blue-700 ring-2 ring-blue-200' :
                            result.cgpa >= 7 ? 'bg-indigo-100 text-indigo-700 ring-2 ring-indigo-200' :
                            result.cgpa >= 6 ? 'bg-yellow-100 text-yellow-700 ring-2 ring-yellow-200' :
                            result.cgpa >= 5 ? 'bg-orange-100 text-orange-700 ring-2 ring-orange-200' :
                            'bg-red-100 text-red-700 ring-2 ring-red-200'
                          }`}>
                            {result.cgpa?.toFixed(2)}
                          </span>
                        </td>
                        <td className="px-4 py-4 text-center">
                          <span className={`px-3 py-1.5 rounded-full text-sm font-bold ${
                            result.grade === 'A+' ? 'bg-green-100 text-green-700' :
                            result.grade === 'A' ? 'bg-blue-100 text-blue-700' :
                            result.grade?.startsWith('B') ? 'bg-indigo-100 text-indigo-700' :
                            result.grade === 'C' ? 'bg-yellow-100 text-yellow-700' :
                            result.grade === 'D' ? 'bg-orange-100 text-orange-700' :
                            'bg-red-100 text-red-700'
                          }`}>
                            {result.grade}
                          </span>
                        </td>
                        <td className="px-4 py-4 text-sm text-gray-600">{result.teacherName}</td>
                        <td className="px-4 py-4">
                          <div className="flex justify-center gap-3">
                            <button className="text-blue-600 hover:text-blue-800 transition">
                              <Edit className="w-5 h-5" />
                            </button>
                            <button className="text-red-600 hover:text-red-800 transition">
                              <Trash2 className="w-5 h-5" />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Subjects Tab */}
          {activeTab === 'subjects' && (
            <div className="bg-white rounded-xl shadow-lg p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">Subjects Management</h2>
                <button className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white px-6 py-2.5 rounded-lg flex items-center gap-2 hover:shadow-lg transform hover:scale-105 transition">
                  <Plus className="w-5 h-5" />
                  Add Subject
                </button>
              </div>

              <div className="overflow-x-auto rounded-lg border border-gray-200">
                <table className="w-full">
                  <thead className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white">
                    <tr>
                      <th className="px-6 py-4 text-left font-semibold">Subject Code</th>
                      <th className="px-6 py-4 text-left font-semibold">Subject Name</th>
                      <th className="px-6 py-4 text-left font-semibold">Class</th>
                      <th className="px-6 py-4 text-center font-semibold">Total Marks</th>
                      <th className="px-6 py-4 text-left font-semibold">Assigned Teacher</th>
                      <th className="px-6 py-4 text-center font-semibold">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-200">
                    {subjects.map((subject, index) => (
                      <tr key={subject.id} className={`${index % 2 === 0 ? 'bg-gray-50' : 'bg-white'} hover:bg-purple-50 transition`}>
                        <td className="px-6 py-4 font-semibold text-purple-600">{subject.subjectCode}</td>
                        <td className="px-6 py-4 font-medium text-gray-900">{subject.subjectName}</td>
                        <td className="px-6 py-4 text-gray-700">{subject.className}</td>
                        <td className="px-6 py-4 text-center font-bold text-gray-900">{subject.totalMarks}</td>
                        <td className="px-6 py-4 text-gray-700">{subject.teacher?.name || 'Not Assigned'}</td>
                        <td className="px-6 py-4">
                          <div className="flex justify-center gap-3">
                            <button className="text-blue-600 hover:text-blue-800 transition">
                              <Edit className="w-5 h-5" />
                            </button>
                            <button className="text-red-600 hover:text-red-800 transition">
                              <Trash2 className="w-5 h-5" />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

// Helper Components
function NavItem({ icon, label, active, onClick }) {
  return (
    <button
      onClick={onClick}
      className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg transition-all ${
        active
          ? 'bg-white text-purple-600 shadow-lg font-semibold'
          : 'text-purple-100 hover:bg-purple-500 hover:text-white'
      }`}
    >
      {icon}
      <span>{label}</span>
    </button>
  );
}

function StatCard({ icon, title, value, color }) {
  const gradients = {
    blue: 'from-blue-500 to-blue-600',
    green: 'from-green-500 to-green-600',
    purple: 'from-purple-500 to-purple-600',
    orange: 'from-orange-500 to-orange-600'
  };

  return (
    <div className={`bg-gradient-to-br ${gradients[color]} rounded-xl shadow-lg p-6 text-white transform hover:scale-105 transition-all duration-300 hover:shadow-2xl`}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-white/80 font-medium mb-1">{title}</p>
          <p className="text-4xl font-bold">{value}</p>
        </div>
        <div className="opacity-80">{icon}</div>
      </div>
    </div>
  );
}

