}

// Admin Dashboard
function AdminDashboard({ user = {}, onLogout = () => {} }) {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [students, setStudents] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [results, setResults] = useState([]);
  const [stats, setStats] = useState({});

  // Search states
  const [studentSearch, setStudentSearch] = useState('');
  const [teacherSearch, setTeacherSearch] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [resultSearch, setResultSearch] = useState('');

  // Filtered data
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [filteredTeachers, setFilteredTeachers] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    // Filter students
    if (studentSearch) {
      const filtered = students.filter(s => 
        s.name?.toLowerCase().includes(studentSearch.toLowerCase()) ||
        s.rollNo?.toLowerCase().includes(studentSearch.toLowerCase()) ||
        s.email?.toLowerCase().includes(studentSearch.toLowerCase())
      );
      setFilteredStudents(filtered);
    } else {
      setFilteredStudents(students);
    }
  }, [studentSearch, students]);

  useEffect(() => {
    // Filter teachers
    let filtered = teachers;

    if (teacherSearch) {
      filtered = filtered.filter(t => 
        t.name?.toLowerCase().includes(teacherSearch.toLowerCase()) ||
        t.teacherId?.toLowerCase().includes(teacherSearch.toLowerCase()) ||
        t.email?.toLowerCase().includes(teacherSearch.toLowerCase())
      );
    }

    if (departmentFilter) {
      filtered = filtered.filter(t => 
        t.department?.toLowerCase().includes(departmentFilter.toLowerCase())
      );
    }

    setFilteredTeachers(filtered);
  }, [teacherSearch, departmentFilter, teachers]);

  useEffect(() => {
    // Filter results
    if (resultSearch) {
      const filtered = results.filter(r => 
        r.studentName?.toLowerCase().includes(resultSearch.toLowerCase()) ||
        r.rollNo?.toLowerCase().includes(resultSearch.toLowerCase()) ||
        r.subjectName?.toLowerCase().includes(resultSearch.toLowerCase())
      );
      setFilteredResults(filtered);
    } else {
      setFilteredResults(results);
    }
  }, [resultSearch, results]);

  const fetchData = async () => {
    try {
      const [studentsRes, teachersRes, subjectsRes, resultsRes] = await Promise.all([
        fetch('http://localhost:8080/api/students'),
        fetch('http://localhost:8080/api/teachers'),
        fetch('http://localhost:8080/api/subjects'),
        fetch('http://localhost:8080/api/results/dto')
      ]);

      const studentsData = await studentsRes.json();
      const teachersData = await teachersRes.json();
      const subjectsData = await subjectsRes.json();
      const resultsData = await resultsRes.json();

      setStudents(studentsData);
      setTeachers(teachersData);
      setSubjects(subjectsData);
      setResults(resultsData);

      setFilteredStudents(studentsData);
      setFilteredTeachers(teachersData);
      setFilteredResults(resultsData);

      setStats({
        totalStudents: studentsData.length,
        totalTeachers: teachersData.length,
        totalSubjects: subjectsData.length,
        totalResults: resultsData.length
      });
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Get unique departments
  const departments = [...new Set(teachers.map(t => t.department).filter(Boolean))];

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-purple-50 via-white to-blue-50">
      {/* Sidebar */}
