                      <td className="px-4 py-3 font-medium text-purple-600">{result.rollNo}</td>
                      <td className="px-4 py-3">{result.studentName}</td>
                      <td className="px-4 py-3">{result.subjectName}</td>
                      <td className="px-4 py-3 text-center">
                        <span className="font-semibold">{result.marksObtained}</span>
                        <span className="text-gray-500">/{result.totalMarks}</span>
                      </td>
                      <td className="px-4 py-3 text-center">
                        <span className={`font-semibold ${
                          result.percentage >= 90 ? 'text-green-600' :
                          result.percentage >= 70 ? 'text-blue-600' :
                          result.percentage >= 50 ? 'text-orange-600' :
                          'text-red-600'
                        }`}>
                          {result.percentage.toFixed(2)}%
                        </span>
                      </td>
                      <td className="px-4 py-3 text-center">
                        <span className={`px-3 py-1 rounded-full text-sm font-bold ${
                          result.cgpa >= 9 ? 'bg-green-100 text-green-700' :
                          result.cgpa >= 8 ? 'bg-blue-100 text-blue-700' :
                          result.cgpa >= 7 ? 'bg-indigo-100 text-indigo-700' :
                          result.cgpa >= 6 ? 'bg-yellow-100 text-yellow-700' :
                          result.cgpa >= 5 ? 'bg-orange-100 text-orange-700' :
                          'bg-red-100 text-red-700'
                        }`}>
                          {result.cgpa.toFixed(2)}
                        </span>
                      </td>
                      <td className="px-4 py-3 text-center">
                        <span className={`px-3 py-1 rounded-full text-sm font-bold ${
                          result.grade === 'A+' ? 'bg-green-100 text-green-700' :
                          result.grade === 'A' ? 'bg-blue-100 text-blue-700' :
                          result.grade.startsWith('B') ? 'bg-indigo-100 text-indigo-700' :
                          result.grade === 'C' ? 'bg-yellow-100 text-yellow-700' :
                          result.grade === 'D' ? 'bg-orange-100 text-orange-700' :
                          'bg-red-100 text-red-700'
                        }`}>
                          {result.grade}
                        </span>
                      </td>
                      <td className="px-4 py-3 text-sm">{result.teacherName}</td>
                      <td className="px-4 py-3">
                        <div className="flex justify-center gap-2">
                          <button className="text-blue-600 hover:text-blue-800">
                            <Edit className="w-4 h-4" />
                          </button>
                          <button className="text-red-600 hover:text-red-800">
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// Helper Components
function StatCard({ icon, title, value, color }) {
  const colorClasses = {
    blue: 'from-blue-500 to-blue-600',
    green: 'from-green-500 to-green-600',
    purple: 'from-purple-500 to-purple-600',
    orange: 'from-orange-500 to-orange-600'
  };

  return (
    <div className={`bg-gradient-to-r ${colorClasses[color]} text-white rounded-lg shadow-lg p-6 transform hover:scale-105 transition`}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-white opacity-90 text-sm font-medium">{title}</p>
          <p className="text-4xl font-bold mt-2">{value || 0}</p>
        </div>
        <div className="opacity-80">{icon}</div>
      </div>
    </div>
  );
}

function TabButton({ active, onClick, icon, label }) {
  return (
    <button
      onClick={onClick}
      className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition ${
        active
          ? 'bg-gradient-to-r from-purple-600 to-indigo-600 text-white shadow-lg'
          : 'text-gray-600 hover:bg-gray-100'
      }`}
    >
      {icon}
      <span>{label}</span>
    </button>
  );
}

