                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Assign Teacher</label>
                <select
                  className="form-input"
                  value={formData.teacher?.id || ''}
                  onChange={(e) => {
                    const teacher = teachers.find(t => t.id === parseInt(e.target.value));
